import { SET_TODO } from './../'

const initialState = {
  item: 'Learn about reducers',
  completed: false,
  id: 3892987589
}

const reducer = (state, action) => {
  switch(action.type) {
    case(SET_TODO):
      return({...state, todo:action.payload})
    default:
      return(state);
  }
}

export {reducer, initialState};
