import { SET_TODO } from './../actions/actions'

const initialState = [{
  item: 'Learn about reducers',
  completed: false,
  id: 3892987589
}]

const reducer = (state, action) => {
  switch(action.type) {
    case(SET_TODO):
      return({...state, odo:action.payload})
    default:
      return(state);
  }
}

export {reducer, initialState};
