import { CLEAR_TODO, SET_TODO, TOGGLE_TODO, ADD_TODO } from "./../actions/actions";

const initialState = {
  todos: [
    {
      title: "Learn about reducers",
      completed: false,
      id: 3892987589,
    },
  ],
  newTodos: "",
};

const reducer = (state, action) => {
  switch (action.type) {
    case SET_TODO:
      return { ...state, newTodos: action.payload };
    case ADD_TODO:
      return {
        ...state, todos: [...state.todos, {
          title: action.payload,
          completed: false,
          id: new Date(),
        }]
      }

    case TOGGLE_TODO:
      return { ...state, toggleTodo: action.payload };

    case CLEAR_TODO:
      return { ...state, clearTodo: action.payload };

    default:
      return state;
  }
};
const reducerExport = { reducer, initialState };
export default reducerExport;
